import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [timezone, setTimezone] = useState('');
  const [friendUsername, setFriendUsername] = useState('');
  const [friends, setFriends] = useState([]);
  const [newEvent, setNewEvent] = useState({ title: '', startTime: '', endTime: '' });

  // Register new user
  const handleRegister = async () => {
    try {
      const response = await axios.post('http://localhost:5000/register', { username, password, timezone });
      setCurrentUser(response.data);
      setUsername('');
      setPassword('');
      setTimezone('');
    } catch (error) {
      alert(error.response.data.message);
    }
  };

  // Login user
  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', { username, password });
      setCurrentUser(response.data);
      setUsername('');
      setPassword('');
      // Fetch user's friends after login
      const friendsResponse = await axios.get(`http://localhost:5000/friends/${response.data.id}`);
      setFriends(friendsResponse.data);
    } catch (error) {
      alert('Invalid credentials');
    }
  };

  // Add a new event to the user's schedule
  const handleAddEvent = async () => {
    try {
      await axios.post('http://localhost:5000/add-event', {
        userId: currentUser.id,
        title: newEvent.title,
        startTime: newEvent.startTime,
        endTime: newEvent.endTime
      });
      alert('Event added');
      setNewEvent({ title: '', startTime: '', endTime: '' });
    } catch (error) {
      alert('Error adding event');
    }
  };

  // Logout
  const handleLogout = () => {
    setCurrentUser(null);
    setFriends([]);
  };

  return (
    <div>
      <h1>ScheduleMate</h1>
      {!currentUser ? (
        <div>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={e => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />
          <input
            type="text"
            placeholder="Timezone"
            value={timezone}
            onChange={e => setTimezone(e.target.value)}
          />
          <button onClick={handleLogin}>Login</button>
          <button onClick={handleRegister}>Register</button>
        </div>
      ) : (
        <div>
          <h1>Welcome, {currentUser.username}</h1>
          <button onClick={handleLogout}>Logout</button>
          <h2>Your Friends</h2>
          <ul>
            {friends.map(friend => (
              <li key={friend.id}>{friend.username}</li>
            ))}
          </ul>
          <h3>Add Event to Your Calendar</h3>
          <input
            type="text"
            placeholder="Event Title"
            value={newEvent.title}
            onChange={e => setNewEvent({ ...newEvent, title: e.target.value })}
          />
          <input
            type="datetime-local"
            placeholder="Start Time"
            value={newEvent.startTime}
            onChange={e => setNewEvent({ ...newEvent, startTime: e.target.value })}
          />
          <input
            type="datetime-local"
            placeholder="End Time"
            value={newEvent.endTime}
            onChange={e => setNewEvent({ ...newEvent, endTime: e.target.value })}
          />
          <button onClick={handleAddEvent}>Add Event</button>
        </div>
      )}
    </div>
  );
}

export default App;
