import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

// Example list of time zones
const timezones = [
  "America/New_York",
  "America/Chicago",
  "America/Los_Angeles",
  "Europe/London",
  "Europe/Paris",
  "Asia/Dubai",
  "Asia/Tokyo",
  "Asia/Kolkata",
  "Australia/Sydney",
  "UTC"
];

// Set up moment localizer for the calendar
const localizer = momentLocalizer(moment);

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [timezone, setTimezone] = useState('');
  const [friendUsername, setFriendUsername] = useState('');
  const [friends, setFriends] = useState([]);
  const [newEvent, setNewEvent] = useState({ title: '', startTime: '', endTime: '' });
  const [events, setEvents] = useState([]);  // To store the user's calendar events

  // Check localStorage for user info when the app loads
  useEffect(() => {
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      const user = JSON.parse(storedUser);
      setCurrentUser(user);
      // Fetch user's friends and schedule if logged in
      fetchFriendsAndEvents(user.id);
    }
  }, []);

  // Function to fetch user's friends and events
  const fetchFriendsAndEvents = async (userId) => {
    try {
      const friendsResponse = await axios.get(`http://localhost:5000/friends/${userId}`);
      setFriends(friendsResponse.data);

      const eventsResponse = await axios.get(`http://localhost:5000/schedule/${userId}`);
      setEvents(
        eventsResponse.data.map(event => ({
          title: event.title,
          start: new Date(event.start_time),
          end: new Date(event.end_time),
        }))
      );
    } catch (error) {
      console.error('Error fetching friends and events:', error);
    }
  };

  // Register new user with timezone
  const handleRegister = async () => {
    try {
      const response = await axios.post('http://localhost:5000/register', { username, password, timezone });
      setCurrentUser(response.data);
      localStorage.setItem('currentUser', JSON.stringify(response.data)); // Store user info in localStorage
      setUsername('');
      setPassword('');
      setTimezone('');
      fetchFriendsAndEvents(response.data.id); // Fetch friends and events after login
    } catch (error) {
      alert(error.response.data.message);
    }
  };

  // Login user
  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', { username, password });
      setCurrentUser(response.data);
      localStorage.setItem('currentUser', JSON.stringify(response.data)); // Store user info in localStorage
      setUsername('');
      setPassword('');
      fetchFriendsAndEvents(response.data.id); // Fetch friends and events after login
    } catch (error) {
      alert('Invalid credentials');
    }
  };

  // Logout user
  const handleLogout = () => {
    setCurrentUser(null);
    localStorage.removeItem('currentUser'); // Clear the localStorage
    setFriends([]);
    setEvents([]);
  };

  // Add a new event
  const handleAddEvent = () => {
    axios.post('http://localhost:5000/add-event', { userId: currentUser.id, ...newEvent })
      .then(response => {
        setNewEvent({ title: '', startTime: '', endTime: '' });
        setEvents([...events, {
          title: response.data.title,
          start: new Date(response.data.start_time),
          end: new Date(response.data.end_time),
        }]);
      })
      .catch(error => {
        alert(error.response.data.message);
      });
  };

  // Add a friend by username
  const handleAddFriend = () => {
    axios.post('http://localhost:5000/add-friend', { userId: currentUser.id, friendUsername })
      .then(response => {
        alert(`${friendUsername} added as a friend`);
        setFriendUsername('');
        fetchFriendsAndEvents(currentUser.id); // Fetch updated friends list
      })
      .catch(error => {
        alert(error.response.data.message);
      });
  };

  return (
    <div>
      <h1>ScheduleMate</h1>
      {!currentUser ? (
        <div>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={e => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />

          {/* Dropdown for Timezone */}
          <select value={timezone} onChange={e => setTimezone(e.target.value)}>
            <option value="">Select Timezone</option>
            {timezones.map(tz => (
              <option key={tz} value={tz}>
                {tz}
              </option>
            ))}
          </select>

          <button onClick={handleLogin}>Login</button>
          <button onClick={handleRegister}>Register</button>
        </div>
      ) : (
        <div>
          <h1>Welcome, {currentUser.username}</h1>
          <button onClick={handleLogout}>Logout</button>

          <h2>Your Friends</h2>
          <ul>
            {friends.map(friend => (
              <li key={friend.id}>{friend.username}</li>
            ))}
          </ul>

          <h2>Add a Friend</h2>
          <input
            type="text"
            placeholder="Friend's Username"
            value={friendUsername}
            onChange={e => setFriendUsername(e.target.value)}
          />
          <button onClick={handleAddFriend}>Add Friend</button>

          <h3>Add Event to Your Calendar</h3>
          <input
            type="text"
            placeholder="Event Title"
            value={newEvent.title}
            onChange={e => setNewEvent({ ...newEvent, title: e.target.value })}
          />
          <input
            type="datetime-local"
            placeholder="Start Time"
            value={newEvent.startTime}
            onChange={e => setNewEvent({ ...newEvent, startTime: e.target.value })}
          />
          <input
            type="datetime-local"
            placeholder="End Time"
            value={newEvent.endTime}
            onChange={e => setNewEvent({ ...newEvent, endTime: e.target.value })}
          />
          <button onClick={handleAddEvent}>Add Event</button>

          <h2>Your Calendar</h2>
          <Calendar
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            style={{ height: 500, margin: "50px" }}
          />
        </div>
      )}
    </div>
  );
}

export default App;
