import React, { useState, useEffect } from 'react';

// Simulate user database in localStorage
const getUsersFromStorage = () => JSON.parse(localStorage.getItem('users')) || [];
const getCurrentUserFromStorage = () => JSON.parse(localStorage.getItem('currentUser')) || null;

function App() {
  const [currentUser, setCurrentUser] = useState(getCurrentUserFromStorage());
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [friendUsername, setFriendUsername] = useState('');
  const [users, setUsers] = useState(getUsersFromStorage());
  const [friends, setFriends] = useState(currentUser ? currentUser.friends : []);
  const [selectedFriend, setSelectedFriend] = useState(null);
  const [newEvent, setNewEvent] = useState({ title: '', date: '', time: '' });

  // Save users and current user to localStorage
  useEffect(() => {
    localStorage.setItem('users', JSON.stringify(users));
    localStorage.setItem('currentUser', JSON.stringify(currentUser));
  }, [users, currentUser]);

  // Register new user
  const handleRegister = () => {
    if (users.find(user => user.username === username)) {
      alert('Username already exists.');
    } else {
      const newUser = { id: Date.now(), username, password, friends: [], calendar: [] };
      setUsers([...users, newUser]);
      setCurrentUser(newUser);
    }
  };

  // Login existing user
  const handleLogin = () => {
    const user = users.find(user => user.username === username && user.password === password);
    if (user) {
      setCurrentUser(user);
      setFriends(user.friends);
    } else {
      alert('Invalid credentials.');
    }
  };

  // Add friend by username
  const handleAddFriend = () => {
    const friend = users.find(user => user.username === friendUsername);
    if (friend) {
      if (!currentUser.friends.includes(friend.id)) {
        const updatedUser = {
          ...currentUser,
          friends: [...currentUser.friends, friend.id],
        };
        setCurrentUser(updatedUser);
        setFriends(updatedUser.friends);
        setUsers(users.map(u => (u.id === currentUser.id ? updatedUser : u)));
      } else {
        alert('User is already your friend.');
      }
    } else {
      alert('User not found.');
    }
    setFriendUsername('');
  };

  // Add event to the current user's calendar
  const handleAddEvent = () => {
    if (newEvent.title && newEvent.date && newEvent.time) {
      const updatedUser = {
        ...currentUser,
        calendar: [...currentUser.calendar, newEvent],
      };
      setCurrentUser(updatedUser);
      setUsers(users.map(u => (u.id === currentUser.id ? updatedUser : u)));
      setNewEvent({ title: '', date: '', time: '' }); // Reset the input fields
    } else {
      alert('Please fill in all event details.');
    }
  };

  // Select friend to view their calendar
  const handleViewFriendCalendar = (friendID) => {
    const friend = users.find(user => user.id === friendID);
    setSelectedFriend(friend);
  };

  // Logout
  const handleLogout = () => {
    setCurrentUser(null);
    setFriends([]);
    setSelectedFriend(null);
  };

  if (!currentUser) {
    return (
      <div>
        <h1>ScheduleMate</h1>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={e => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <button onClick={handleLogin}>Login</button>
        <button onClick={handleRegister}>Register</button>
      </div>
    );
  }

  return (
    <div>
      <h1>Welcome, {currentUser.username}</h1>
      <button onClick={handleLogout}>Logout</button>

      <h2>Your Calendar</h2>
      <ul>
        {currentUser.calendar.map((event, index) => (
          <li key={index}>
            {event.title} - {event.date} at {event.time}
          </li>
        ))}
      </ul>

      <h3>Add Event to Your Calendar</h3>
      <input
        type="text"
        placeholder="Event Title"
        value={newEvent.title}
        onChange={e => setNewEvent({ ...newEvent, title: e.target.value })}
      />
      <input
        type="date"
        value={newEvent.date}
        onChange={e => setNewEvent({ ...newEvent, date: e.target.value })}
      />
      <input
        type="time"
        value={newEvent.time}
        onChange={e => setNewEvent({ ...newEvent, time: e.target.value })}
      />
      <button onClick={handleAddEvent}>Add Event</button>

      <h2>Friends List</h2>
      <ul>
        {friends.map(friendID => {
          const friend = users.find(user => user.id === friendID);
          return (
            <li key={friendID}>
              {friend ? friend.username : 'Unknown User'}{' '}
              <button onClick={() => handleViewFriendCalendar(friendID)}>View Calendar</button>
            </li>
          );
        })}
      </ul>

      {selectedFriend && (
        <div>
          <h2>{selectedFriend.username}'s Calendar</h2>
          <ul>
            {selectedFriend.calendar.length > 0 ? (
              selectedFriend.calendar.map((event, index) => (
                <li key={index}>
                  {event.title} - {event.date} at {event.time}
                </li>
              ))
            ) : (
              <p>No events scheduled.</p>
            )}
          </ul>
        </div>
      )}

      <h3>Add Friend by Username</h3>
      <input
        type="text"
        placeholder="Friend's Username"
        value={friendUsername}
        onChange={e => setFriendUsername(e.target.value)}
      />
      <button onClick={handleAddFriend}>Add Friend</button>
    </div>
  );
}

export default App;
