import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

// Example list of time zones
const timezones = [
  "America/New_York", "America/Chicago", "America/Los_Angeles", "Europe/London", "Europe/Paris",
  "Asia/Dubai", "Asia/Tokyo", "Asia/Kolkata", "Australia/Sydney", "UTC"
];

const localizer = momentLocalizer(moment);

const TIME_API_URL = "https://timeapi.io/api/Conversion/ConvertTimeZone";

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [timezone, setTimezone] = useState('');
  const [friendUsername, setFriendUsername] = useState('');
  const [friends, setFriends] = useState([]);
  const [newEvent, setNewEvent] = useState({ title: '', startTime: '', endTime: '' });
  const [events, setEvents] = useState([]);
  const [friendEvents, setFriendEvents] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isCallModalOpen, setIsCallModalOpen] = useState(false); // Modal for call duration
  const [callDuration, setCallDuration] = useState(''); // Call duration input
  const [optimalTimes, setOptimalTimes] = useState([]); // Store optimal times

  useEffect(() => {
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      const user = JSON.parse(storedUser);
      setCurrentUser(user);
      fetchFriendsAndEvents(user.id);
    }
  }, []);

  const fetchFriendsAndEvents = async (userId) => {
    try {
      const friendsResponse = await axios.get(`http://localhost:5000/friends/${userId}`);
      setFriends(friendsResponse.data);

      const eventsResponse = await axios.get(`http://localhost:5000/schedule/${userId}`);
      setEvents(
        eventsResponse.data.map(event => ({
          title: event.title,
          start: new Date(event.start_time),
          end: new Date(event.end_time),
        }))
      );
    } catch (error) {
      console.error('Error fetching friends and events:', error);
    }
  };

  const handleRegister = async () => {
    try {
      const response = await axios.post('http://localhost:5000/register', { username, password, timezone });
      setCurrentUser(response.data);
      localStorage.setItem('currentUser', JSON.stringify(response.data));
      setUsername('');
      setPassword('');
      setTimezone('');
      fetchFriendsAndEvents(response.data.id);
    } catch (error) {
      alert(error.response.data.message);
    }
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', { username, password });
      setCurrentUser(response.data);
      localStorage.setItem('currentUser', JSON.stringify(response.data));
      setUsername('');
      setPassword('');
      fetchFriendsAndEvents(response.data.id);
    } catch (error) {
      alert('Invalid credentials');
    }
  };

  const handleLogout = () => {
    setCurrentUser(null);
    localStorage.removeItem('currentUser');
    setFriends([]);
    setEvents([]);
  };

  const handleAddEvent = () => {
    const utcStartTime = new Date(newEvent.startTime).toISOString();
    const utcEndTime = new Date(newEvent.endTime).toISOString();

    axios.post('http://localhost:5000/add-event', {
      userId: currentUser.id,
      title: newEvent.title,
      startTime: utcStartTime,
      endTime: utcEndTime
    })
    .then(response => {
      setNewEvent({ title: '', startTime: '', endTime: '' });
      setEvents([...events, {
        title: response.data.title,
        start: new Date(response.data.start_time),
        end: new Date(response.data.end_time),
      }]);
    })
    .catch(error => {
      alert(error.response.data.message);
    });
  };

  // Use this function in the "Add Friend" button to avoid the warning
  const handleAddFriend = () => {
    axios.post('http://localhost:5000/add-friend', { userId: currentUser.id, friendUsername })
      .then(response => {
        alert(`${friendUsername} added as a friend`);
        setFriendUsername('');
        fetchFriendsAndEvents(currentUser.id);
      })
      .catch(error => {
        alert(error.response.data.message);
      });
  };

  const handleShowFriendCalendar = async (friendId) => {
    try {
      const response = await axios.get(`http://localhost:5000/schedule/${friendId}`);
      const friendResponse = await axios.get(`http://localhost:5000/user/${friendId}`);
      const friendTimezone = friendResponse.data.timezone;  // Fixed: friendTimezone is now used in the API call

      const events = await Promise.all(response.data.map(async (event) => {
        // Use friendTimezone for API conversion
        const startTime = await convertTimeToUserTimezone(event.start_time, friendTimezone, currentUser.timezone);
        const endTime = await convertTimeToUserTimezone(event.end_time, friendTimezone, currentUser.timezone);

        return {
          title: event.title,
          start: startTime,
          end: endTime,
        };
      }));

      setFriendEvents(events);
      setIsModalOpen(true);
    } catch (error) {
      console.error('Error fetching friend\'s schedule:', error);
    }
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const convertTimeToUserTimezone = async (time, fromTimezone, toTimezone) => {
    try {
      const response = await axios.post(TIME_API_URL, {
        fromTimeZone: fromTimezone,
        dateTime: new Date(time).toISOString(),
        toTimeZone: toTimezone,
        dstAmbiguity: ""
      });

      return new Date(response.data.conversionResult.dateTime);
    } catch (error) {
      console.error('Error converting time zone:', error);
      return new Date(time);
    }
  };

  return (
    <div>
      <h1>ScheduleMate</h1>
      {!currentUser ? (
        <div>
          <input type="text" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
          <select value={timezone} onChange={e => setTimezone(e.target.value)}>
            <option value="">Select Timezone</option>
            {timezones.map(tz => (
              <option key={tz} value={tz}>{tz}</option>
            ))}
          </select>
          <button onClick={handleLogin}>Login</button>
          <button onClick={handleRegister}>Register</button>
        </div>
      ) : (
        <div>
          <h1>Welcome, {currentUser.username}</h1>
          <button onClick={handleLogout}>Logout</button>

          <h2>Your Friends</h2>
          <ul>
            {friends.map(friend => (
              <li key={friend.id}>
                {friend.username}
                <button onClick={() => handleShowFriendCalendar(friend.id)}>View Calendar</button>
                {/* Add Friend logic attached here */}
                <button onClick={handleAddFriend}>Add Friend</button>
              </li>
            ))}
          </ul>

          <h3>Add Event to Your Calendar</h3>
          <input type="text" placeholder="Event Title" value={newEvent.title} onChange={e => setNewEvent({ ...newEvent, title: e.target.value })} />
          <input type="datetime-local" placeholder="Start Time" value={newEvent.startTime} onChange={e => setNewEvent({ ...newEvent, startTime: e.target.value })} />
          <input type="datetime-local" placeholder="End Time" value={newEvent.endTime} onChange={e => setNewEvent({ ...newEvent, endTime: e.target.value })} />
          <button onClick={handleAddEvent}>Add Event</button>

          <h2>Your Calendar</h2>
          <Calendar localizer={localizer} events={events} startAccessor="start" endAccessor="end" style={{ height: 500, margin: "50px" }} />

          <Modal isOpen={isModalOpen} onClose={handleCloseModal}>
            <h2>Friend's Calendar</h2>
            <Calendar localizer={localizer} events={friendEvents} startAccessor="start" endAccessor="end" style={{ height: 400 }} />
          </Modal>
        </div>
      )}
    </div>
  );
}

export default App;

// Modal Component
const Modal = ({ isOpen, onClose, children }) => {
  if (!isOpen) return null;

  return (
    <div style={modalStyles.overlay}>
      <div style={modalStyles.modal}>
        <button onClick={onClose} style={modalStyles.closeButton}>Close</button>
        {children}
      </div>
    </div>
  );
};

const modalStyles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.7)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000,
  },
  modal: {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '8px',
    maxWidth: '600px',
    width: '100%',
  },
  closeButton: {
    float: 'right',
    backgroundColor: '#f44336',
    color: 'white',
    border: 'none',
    padding: '5px 10px',
    cursor: 'pointer',
    borderRadius: '5px',
  },
};
